mapper.select:
insert into
board(user_seq, title, content, upload_date)
values(#{user_seq}, #{title}, #{content},now());

select * from board
	
		where user_seq=#{user_seq}
	
order by board_seq desc limit #{displayPost}, #{postNum}

update post set post_title =
#{post_title}, price = #{price}, post_content=#{post_content}, prod_status = #{prod_status}, upload_date = now()
create_date = now() where post_seq = #{post_seq};

select
count(*) from likePost where post_seq = #{post_seq} and user_seq =
#{user_seq};

select
likeCnt from likePost where post_seq = #{post_seq} and user_seq =
#{user_seq};

select * from ( select post_seq, post_title, price, upload_date, pay_check, location1 from post
where post_title like concat('%',#{keyword},'%')
order by post_seq desc) as T1 limit #{displayPost},#{postNum}
